import React, { useState, useEffect, useRef } from 'react';
import Tree from 'react-d3-tree';

// ======== MUIコンポーネント & フレームワーク =========
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Drawer,
  List,
  ListItemButton,
  ListItemText,
  Box,
  Paper,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  CircularProgress,
  Container
} from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

// ======== Framer Motion for Animations =========
import { motion, AnimatePresence } from 'framer-motion';

/**
 * テーマを作成（都会的なブルー×ホワイト＋やや近未来感）
 */
const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2', // MaterialUI既定のブルーを採用
    },
    secondary: {
      main: '#00f2fe', // 近未来ぽいライトブルー
    },
  },
  typography: {
    fontFamily: [
      'Montserrat',
      'Playfair Display',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
    ].join(','),
  },
});

/**
 * アニメーション用のバリアント例
 */
const fadeInVariants = {
  hidden: { opacity: 0, y: -10 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.5 } },
};

const popInVariants = {
  hidden: { opacity: 0, scale: 0.9 },
  visible: { opacity: 1, scale: 1, transition: { duration: 0.4 } },
};

function App() {
  // ローカル用APIエンドポイント
  const API_URL = "http://localhost:8000";

  // --------------------------
  // ステート定義（既存）
  // --------------------------
  const [notes, setNotes] = useState('ここに文字起こしが表示されます');
  const [summary, setSummary] = useState('ここに議事録・改善案が表示されます');
  const [mindmapData, setMindmapData] = useState(null);
  const [title, setTitle] = useState('');

  const [recording, setRecording] = useState(false);
  const [recorded, setRecorded] = useState(false);

  // モーダル関連
  const [showSaveModal, setShowSaveModal] = useState(false);

  // ローディングと保存ステータス
  const [loading, setLoading] = useState(false);
  const [saveStatus, setSaveStatus] = useState(null);

  // 保存された議事録（一覧）
  const [savedMinutes, setSavedMinutes] = useState([]);
  const [selectedMinute, setSelectedMinute] = useState(null);

  // サイドバー開閉
  const [sidebarOpen, setSidebarOpen] = useState(true);

  // --------------------------
  // 追加: 録音関連
  // --------------------------
  const mediaRecorderRef = useRef(null);
  const [audioChunks, setAudioChunks] = useState([]); // 複数のBlobを蓄える
  const [recordingTimerId, setRecordingTimerId] = useState(null);
  const [recordingDuration, setRecordingDuration] = useState(0);
  const durationIntervalRef = useRef(null);

  // --------------------------
  // APIから保存済み議事録を取得
  // --------------------------
  const fetchSavedMinutes = async () => {
    try {
      const res = await fetch(`${API_URL}/get-minutes`);
      if (!res.ok) throw new Error('保存された議事録の取得に失敗しました');
      const data = await res.json();
      // 直近のものが上になるようソート（id降順）
      const sorted = data.minutes.sort((a, b) => b.id - a.id);
      setSavedMinutes(sorted);
    } catch (error) {
      console.error('Error fetching saved minutes:', error);
    }
  };

  useEffect(() => {
    fetchSavedMinutes();
  }, []);

  // --------------------------
  // 録音開始
  // --------------------------
  const startRecording = async () => {
    setRecording(true);
    setRecorded(false);
    setNotes('録音が開始されました');
    setSummary('会議中...');

    try {
      // マイクアクセス要求
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);

      // イベント: データがまとまるたびに呼ばれる
      const localChunks = [];
      mediaRecorder.addEventListener("dataavailable", event => {
        if (event.data.size > 0) {
          localChunks.push(event.data);
        }
      });

      // イベント: stopが呼ばれたら1つのBlobにまとめる
      mediaRecorder.addEventListener("stop", () => {
        const blob = new Blob(localChunks, { type: 'audio/mp3' });
        // すでに保存されているチャンクと合体
        setAudioChunks(prev => [...prev, blob]);
      });

      mediaRecorderRef.current = mediaRecorder;
      mediaRecorder.start();

      // 10分ごとに強制的に1チャンクを終了→次の録音開始
      const chunkInterval = setInterval(() => {
        if (mediaRecorder.state === "recording") {
          mediaRecorder.stop();
        }
        // 録音をすぐ再開
        const newLocalChunks = [];
        mediaRecorder.addEventListener("dataavailable", event => {
          if (event.data.size > 0) {
            newLocalChunks.push(event.data);
          }
        });
        mediaRecorder.addEventListener("stop", () => {
          const newBlob = new Blob(newLocalChunks, { type: 'audio/mp3' });
          setAudioChunks(prev => [...prev, newBlob]);
        });

        mediaRecorder.start();
      }, 600000); // 10分 = 600,000 ms

      setRecordingTimerId(chunkInterval);

      // 経過時間をカウントするInterval
      const durationTimer = setInterval(() => {
        setRecordingDuration(prev => prev + 1); // 1秒ごとに+1
      }, 1000);
      durationIntervalRef.current = durationTimer;

    } catch (err) {
      console.error('録音の開始に失敗:', err);
      setNotes('マイクが許可されていません');
      setRecording(false);
    }
  };

  // --------------------------
  // 録音停止
  // --------------------------
  const stopRecording = () => {
    setRecording(false);
    setRecorded(true);

    if (recordingTimerId) {
      clearInterval(recordingTimerId);
      setRecordingTimerId(null);
    }
    if (durationIntervalRef.current) {
      clearInterval(durationIntervalRef.current);
      durationIntervalRef.current = null;
    }

    if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
      mediaRecorderRef.current.stop();
    }

    setNotes('録音が停止しました。文字起こしを準備中...');
  };

  // --------------------------
  // 録音データ(複数チャンク)をサーバーへ送信し、議事録生成
  // --------------------------
  const sendChunksToServer = async () => {
    if (!audioChunks || audioChunks.length === 0) {
      console.error('音声がありません');
      return;
    }

    const formData = new FormData();
    audioChunks.forEach((blob, index) => {
      formData.append('audios', blob, `recording_part_${index}.mp3`);
    });

    try {
      setLoading(true);
      const res = await fetch(`${API_URL}/transcribe-chunks`, {
        method: 'POST',
        body: formData,
      });
      if (!res.ok) throw new Error(`サーバーエラー: ${res.statusText}`);
      const data = await res.json();

      setNotes(data.formatted_transcript);
      setSummary(data.analysis);
      setMindmapData(data.mindmap);
      setTitle(data.title);

      // アップロード後は音声チャンク配列をクリア
      setAudioChunks([]);
      setRecorded(true);
    } catch (error) {
      console.error('通信エラー:', error);
      setNotes('通信エラーが発生しました（ファイルサイズ制限の可能性あり）');
    } finally {
      setLoading(false);
    }
  };

  // --------------------------
  // 議事録保存
  // --------------------------
  const saveToDatabase = async () => {
    try {
      const res = await fetch(`${API_URL}/save-minutes`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          formatted_transcript: notes,
          analysis: summary,
          mindmap: mindmapData,
          title: title,
        }),
      });
      if (!res.ok) {
        const errorText = await res.text();
        throw new Error(`保存に失敗しました: ${errorText}`);
      }
      const result = await res.json();
      if (result.status === 'success') {
        setSaveStatus('保存が完了しました！');
        setShowSaveModal(false);
        fetchSavedMinutes();
      } else {
        setSaveStatus('保存に失敗しました: ' + (result.detail || ''));
      }
    } catch (err) {
      console.error(err);
      setSaveStatus(err.message || '保存中にエラーが発生しました');
    }
  };

  // --------------------------
  // モーダル閉じる
  // --------------------------
  const closeSaveModal = () => {
    setSaveStatus(null);
  };

  // --------------------------
  // サイドバーの開閉
  // --------------------------
  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <ThemeProvider theme={theme}>
      <Box
        sx={{
          minHeight: '100vh',
          background: 'linear-gradient(to bottom right, #f0f9ff, #cfe9ff, #eef6ff)',
          overflow: 'hidden',
        }}
      >
        {/* ヘッダー */}
        <AppBar position="sticky" sx={{ bgcolor: 'rgba(255, 255, 255, 0.8)', backdropFilter: 'blur(8px)' }}>
          <Toolbar>
            <Button
              variant="contained"
              color="primary"
              onClick={toggleSidebar}
              sx={{
                mr: 2,
                borderRadius: '50%',
                width: 36,
                minWidth: 36,
                height: 36,
                p: 0,
                fontSize: '1rem',
              }}
            >
              {sidebarOpen ? '←' : '→'}
            </Button>
            <Typography variant="h5" sx={{ flexGrow: 1, color: 'primary.main', fontWeight: 'bold' }}>
              Conect AI
            </Typography>
            {/* 追加でボタンなど必要ならここに */}
          </Toolbar>
        </AppBar>

        {/* サイドバー Drawer */}
        <Drawer
          variant="persistent"
          anchor="left"
          open={sidebarOpen}
          sx={{
            '& .MuiDrawer-paper': {
              width: 280,
              boxSizing: 'border-box',
              borderRight: '1px solid #eee',
              bgcolor: 'rgba(255,255,255,0.85)',
              backdropFilter: 'blur(6px)',
            },
          }}
        >
          <Box sx={{ p: 2 }}>
            <Typography variant="subtitle1" sx={{ mb: 1, fontWeight: 'bold', color: '#1976d2' }}>
              保存した議事録
            </Typography>
            <List>
              {savedMinutes.length > 0 ? (
                savedMinutes.map((minute) => (
                  <ListItemButton
                    key={minute.id}
                    onClick={() => {
                      setSelectedMinute(minute);
                      setShowSaveModal(false);
                    }}
                  >
                    <ListItemText primary={minute.title} />
                  </ListItemButton>
                ))
              ) : (
                <ListItemButton>
                  <ListItemText primary="保存された議事録はありません" />
                </ListItemButton>
              )}
            </List>
          </Box>
        </Drawer>

        {/* メインコンテンツ */}
        <Box
          sx={{
            ml: sidebarOpen ? '280px' : 0,
            p: 3,
            transition: 'margin-left 0.3s ease',
          }}
        >
          <AnimatePresence>
            {selectedMinute ? (
              <motion.div
                key="detailView"
                variants={fadeInVariants}
                initial="hidden"
                animate="visible"
                exit={{ opacity: 0 }}
              >
                {/* 保存済み議事録表示 */}
                <Button
                  variant="contained"
                  onClick={() => setSelectedMinute(null)}
                  sx={{ mb: 2 }}
                >
                  戻る
                </Button>
                <Typography variant="h5" sx={{ fontWeight: 'bold', mb: 2, color: 'primary.main' }}>
                  {selectedMinute.title}
                </Typography>

                <Paper sx={{ p: 2, mb: 2 }}>
                  <Typography variant="subtitle1" sx={{ fontWeight: 'bold', color: 'secondary.main' }}>
                    整形された文字起こし
                  </Typography>
                  <Typography variant="body2">{selectedMinute.formatted_transcript}</Typography>
                </Paper>

                <Paper sx={{ p: 2, mb: 2 }}>
                  <Typography variant="subtitle1" sx={{ fontWeight: 'bold', color: 'secondary.main' }}>
                    議事録・改善案
                  </Typography>
                  <Typography variant="body2">{selectedMinute.analysis}</Typography>
                </Paper>

                {selectedMinute.mindmap && (
                  <Box sx={{ border: '1px solid #ccc', p: 2, borderRadius: 2 }}>
                    <Tree
                      data={selectedMinute.mindmap}
                      orientation="vertical"
                      translate={{ x: 400, y: 50 }}
                      pathFunc="diagonal"
                      separation={{ siblings: 1.5, nonSiblings: 2 }}
                      nodeSize={{ x: 300, y: 100 }}
                    />
                  </Box>
                )}
              </motion.div>
            ) : (
              <motion.div
                key="recorderView"
                variants={fadeInVariants}
                initial="hidden"
                animate="visible"
                exit={{ opacity: 0 }}
              >
                {/* 録音・生成画面 */}
                <Paper sx={{ p: 3, mb: 2 }} elevation={3}>
                  <Typography variant="body1" gutterBottom>
                    このアプリでは、会議の録音、文字起こし、議事録・改善案生成、そしてマインドマップ作成が可能です。
                    <br />
                    「録音開始」ボタンを押して会議内容を録音し、録音停止後に「議事録生成」ボタンを押すと自動生成された議事録が下に表示されます。
                    <br />
                    ※長時間の録音でも、小分けにしてアップロードします。
                  </Typography>

                  {/* 録音開始/停止ボタン */}
                  {!recording ? (
                    <Button
                      variant="contained"
                      color="primary"
                      onClick={startRecording}
                      sx={{ mr: 2 }}
                    >
                      録音開始
                    </Button>
                  ) : (
                    <Button
                      variant="contained"
                      color="secondary"
                      onClick={stopRecording}
                      sx={{ mr: 2 }}
                    >
                      録音停止
                    </Button>
                  )}

                  {/* 経過時間表示 */}
                  {recording && (
                    <Typography variant="body2" sx={{ mt: 1 }}>
                      録音中... 経過時間: {Math.floor(recordingDuration / 60)}分{' '}
                      {recordingDuration % 60}秒
                    </Typography>
                  )}

                  {/* 議事録生成ボタン */}
                  {recorded && (
                    <Box sx={{ mt: 2 }}>
                      <Button variant="outlined" onClick={sendChunksToServer}>
                        議事録生成
                      </Button>
                    </Box>
                  )}
                </Paper>

                {/* 生成結果 */}
                {recorded && notes && (
                  <motion.div
                    key="generatedView"
                    variants={popInVariants}
                    initial="hidden"
                    animate="visible"
                    exit={{ opacity: 0 }}
                  >
                    <Paper sx={{ p: 3, mb: 2 }} elevation={2}>
                      <Typography variant="h6" sx={{ mb: 2, color: 'primary.main' }}>
                        生成された議事録（編集可能）
                      </Typography>

                      <Box sx={{ mb: 2 }}>
                        <Typography variant="subtitle2">タイトル:</Typography>
                        <TextField
                          fullWidth
                          variant="outlined"
                          size="small"
                          sx={{ mt: 1 }}
                          value={title}
                          onChange={(e) => setTitle(e.target.value)}
                        />
                      </Box>

                      <Box sx={{ mb: 2 }}>
                        <Typography variant="subtitle2">文字起こし:</Typography>
                        <TextField
                          fullWidth
                          multiline
                          rows={4}
                          variant="outlined"
                          size="small"
                          sx={{ mt: 1 }}
                          value={notes}
                          onChange={(e) => setNotes(e.target.value)}
                        />
                      </Box>

                      <Box sx={{ mb: 2 }}>
                        <Typography variant="subtitle2">議事録・改善案:</Typography>
                        <TextField
                          fullWidth
                          multiline
                          rows={4}
                          variant="outlined"
                          size="small"
                          sx={{ mt: 1 }}
                          value={summary}
                          onChange={(e) => setSummary(e.target.value)}
                        />
                      </Box>

                      {mindmapData && (
                        <Box sx={{ border: '1px solid #ccc', p: 2, borderRadius: 2 }}>
                          <Tree
                            data={mindmapData}
                            orientation="vertical"
                            translate={{ x: 400, y: 50 }}
                            pathFunc="diagonal"
                            separation={{ siblings: 1.5, nonSiblings: 2 }}
                            nodeSize={{ x: 300, y: 100 }}
                          />
                        </Box>
                      )}

                      <Box sx={{ mt: 2 }}>
                        <Button
                          variant="contained"
                          onClick={() => setShowSaveModal(true)}
                        >
                          確認画面へ
                        </Button>
                      </Box>
                    </Paper>
                  </motion.div>
                )}
              </motion.div>
            )}
          </AnimatePresence>
        </Box>

        {/* 保存用モーダル */}
        <Dialog open={showSaveModal} onClose={() => setShowSaveModal(false)} fullWidth maxWidth="md">
          <DialogTitle>この内容で保存します</DialogTitle>
          <DialogContent dividers>
            <DialogContentText>
              <strong>タイトル:</strong> {title}
            </DialogContentText>
            <DialogContentText>
              <strong>文字起こし:</strong> {notes}
            </DialogContentText>
            <DialogContentText>
              <strong>議事録・改善案:</strong> {summary}
            </DialogContentText>
            {mindmapData && (
              <Box sx={{ border: '1px solid #ccc', p: 2, borderRadius: 2, mt: 2 }}>
                <Tree
                  data={mindmapData}
                  orientation="vertical"
                  translate={{ x: 350, y: 50 }}
                  pathFunc="diagonal"
                  separation={{ siblings: 1.5, nonSiblings: 2 }}
                  nodeSize={{ x: 300, y: 100 }}
                />
              </Box>
            )}
          </DialogContent>
          <DialogActions>
            <Button onClick={saveToDatabase} variant="contained">
              この内容で保存する
            </Button>
            <Button onClick={() => setShowSaveModal(false)} variant="outlined">
              閉じる
            </Button>
          </DialogActions>
        </Dialog>

        {/* ローディング表示 */}
        <Dialog open={loading} maxWidth="xs">
          <DialogContent sx={{ textAlign: 'center' }}>
            <CircularProgress />
            <DialogContentText sx={{ mt: 2 }}>
              議事録を生成中です...お待ちください
            </DialogContentText>
          </DialogContent>
        </Dialog>

        {/* 保存結果モーダル */}
        <Dialog open={Boolean(saveStatus)} onClose={closeSaveModal} maxWidth="xs">
          <DialogContent>
            <DialogContentText>{saveStatus}</DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={closeSaveModal} variant="contained">
              閉じる
            </Button>
          </DialogActions>
        </Dialog>
      </Box>
    </ThemeProvider>
  );
}

export default App;
