import React, { useState, useEffect, useRef } from 'react';
import Tree from 'react-d3-tree';

// ======== MUI & ãã®ä»– ========
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Drawer,
  List,
  ListItemButton,
  ListItemText,
  Box,
  Paper,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  CircularProgress,
  IconButton
} from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { motion, AnimatePresence } from 'framer-motion';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';

/** ãƒ†ãƒ¼ãƒžè¨­å®š */
const theme = createTheme({
  palette: {
    primary: { main: '#1976d2' },
    secondary: { main: '#00f2fe' },
  },
  typography: {
    fontFamily: [
      'Montserrat',
      'Playfair Display',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
    ].join(','),
  },
});

// ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³ç”¨ãƒãƒªã‚¢ãƒ³ãƒˆ
const fadeInVariants = {
  hidden: { opacity: 0, y: -10 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.5 } },
};

function App() {
  const API_URL = "http://localhost:8000";

  // --------------------------
  // ã‚¹ãƒ†ãƒ¼ãƒˆé¡žï¼ˆè­°äº‹éŒ²è¡¨ç¤ºãƒ»ç”Ÿæˆï¼‰
  // --------------------------
  const [notes, setNotes] = useState('ã“ã“ã«æ–‡å­—èµ·ã“ã—ãŒè¡¨ç¤ºã•ã‚Œã¾ã™');
  const [summary, setSummary] = useState('ã“ã“ã«è­°äº‹éŒ²ãƒ»æ”¹å–„æ¡ˆãŒè¡¨ç¤ºã•ã‚Œã¾ã™');
  const [mindmapData, setMindmapData] = useState(null);
  const [title, setTitle] = useState('');
  const [recording, setRecording] = useState(false);   // ç¾åœ¨éŒ²éŸ³ä¸­ã‹
  const [recorded, setRecorded] = useState(false);     // éŒ²éŸ³ã‚’åœæ­¢ã—ãŸãƒ•ãƒ©ã‚°
  const [showSaveModal, setShowSaveModal] = useState(false);
  const [loading, setLoading] = useState(false);
  const [saveStatus, setSaveStatus] = useState(null);

  // ä¿å­˜æ¸ˆã¿è­°äº‹éŒ²
  const [savedMinutes, setSavedMinutes] = useState([]);
  const [selectedMinute, setSelectedMinute] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  // --------------------------
  // è¤‡æ•°ãƒãƒ£ãƒ³ã‚¯éŒ²éŸ³ç”¨
  // --------------------------
  const [audioChunks, setAudioChunks] = useState([]);  // å®Œæˆã—ãŸBlobã®é…åˆ—
  const localChunksRef = useRef([]);                   // ondataavailable ã§å—ã‘å–ã‚‹å°ãƒãƒ£ãƒ³ã‚¯
  const mediaRecorderRef = useRef(null);
  const mediaStreamRef = useRef(null);
  const chunkIntervalRef = useRef(null);

  // --------------------------
  // å…¥åŠ›ãƒ¢ãƒ¼ãƒ‰: audio / text / chat
  // --------------------------
  const [mode, setMode] = useState("audio");
  const [inputTranscript, setInputTranscript] = useState("");

  // ãƒãƒ£ãƒƒãƒˆç”¨
  const [chatMessages, setChatMessages] = useState([]);
  const [chatInput, setChatInput] = useState("");

  // --------------------------
  // ä¿å­˜æ¸ˆã¿è­°äº‹éŒ²å–å¾—
  // --------------------------
  const fetchSavedMinutes = async () => {
    try {
      const res = await fetch(`${API_URL}/get-minutes`);
      if (!res.ok) throw new Error('ä¿å­˜ã•ã‚ŒãŸè­°äº‹éŒ²ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ');
      const data = await res.json();
      const sorted = data.minutes.sort((a, b) => b.id - a.id);
      setSavedMinutes(sorted);
    } catch (error) {
      console.error('Error fetching saved minutes:', error);
    }
  };

  useEffect(() => {
    fetchSavedMinutes();
  }, []);

  // --------------------------
  // éŒ²éŸ³é–‹å§‹ (â€»å¤‰æ›´ã—ãªã„)
  // --------------------------
  const startRecording = async () => {
    setRecording(true);
    setRecorded(false);
    setNotes('éŒ²éŸ³ãŒé–‹å§‹ã•ã‚Œã¾ã—ãŸ');
    setSummary('ä¼šè­°ä¸­...');
    setAudioChunks([]);       
    localChunksRef.current = [];

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaStreamRef.current = stream;

      const mimeType = getSupportedMimeType() || "audio/webm";
      const mediaRecorder = new MediaRecorder(stream, { mimeType });
      mediaRecorderRef.current = mediaRecorder;

      mediaRecorder.addEventListener("dataavailable", (e) => {
        if (e.data.size > 0) {
          console.log("dataavailable chunk size:", e.data.size);
          localChunksRef.current.push(e.data);
        }
      });

      mediaRecorder.addEventListener("stop", () => {
        setTimeout(() => {
          if (localChunksRef.current.length > 0) {
            const blob = new Blob(localChunksRef.current, { type: mimeType });
            console.log("â˜… stop: Blob size =", blob.size);
            setAudioChunks((prev) => [...prev, blob]);
            localChunksRef.current = [];
          }
          if (recording) {
            console.log("â˜… chunk stopâ†’start (10åˆ†åŒºåˆ‡ã‚Š)");
            mediaRecorder.start();
          }
        }, 1000);
      });

      mediaRecorder.start();
      console.log("ðŸŽ™ï¸ éŒ²éŸ³é–‹å§‹");

      chunkIntervalRef.current = setInterval(() => {
        if (mediaRecorder.state === "recording") {
          console.log("=== 10åˆ†çµŒéŽ: stop() ã§åŒºåˆ‡ã‚Šã¾ã™ ===");
          mediaRecorder.stop();
        }
      }, 30000); // 10åˆ† (ä»Šå›žã¯ãƒ†ã‚¹ãƒˆã®ãŸã‚30ç§’)
    } catch (err) {
      console.error('éŒ²éŸ³é–‹å§‹å¤±æ•—:', err);
      setNotes('éŒ²éŸ³é–‹å§‹ã«å¤±æ•—ã—ã¾ã—ãŸ: ' + err.message);
      setRecording(false);
    }
  };

  // ãƒ–ãƒ©ã‚¦ã‚¶ãŒã‚µãƒãƒ¼ãƒˆã™ã‚‹éŒ²éŸ³mimeTypeã‚’é †ã«æŽ¢ã™
  function getSupportedMimeType() {
    const possibleTypes = [
      "audio/webm;codecs=opus",
      "audio/ogg;codecs=opus",
      "audio/webm",
      "audio/ogg"
    ];
    for (const t of possibleTypes) {
      if (MediaRecorder.isTypeSupported(t)) {
        return t;
      }
    }
    return "";
  }

  // --------------------------
  // éŒ²éŸ³åœæ­¢ (â€»å¤‰æ›´ã—ãªã„)
  // --------------------------
  const stopRecording = async () => {
    console.log("ðŸ›‘ éŒ²éŸ³åœæ­¢ãƒœã‚¿ãƒ³æŠ¼ä¸‹");
    setRecording(false);
    setRecorded(true);

    if (chunkIntervalRef.current) {
      clearInterval(chunkIntervalRef.current);
      chunkIntervalRef.current = null;
    }

    if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
      mediaRecorderRef.current.stop();
    }
    if (mediaStreamRef.current) {
      mediaStreamRef.current.getTracks().forEach((track) => track.stop());
    }

    setNotes("éŒ²éŸ³ãŒåœæ­¢ã—ã¾ã—ãŸã€‚æ–‡å­—èµ·ã“ã—ã‚’æº–å‚™ä¸­...");

    setTimeout(() => {
      if (!audioChunks || audioChunks.length === 0) {
        console.warn("âš ï¸ ãƒãƒ£ãƒ³ã‚¯ãŒã‚ã‚Šã¾ã›ã‚“");
        return;
      }
      console.log("âœ… stopå¾Œ: audioChunks.length =", audioChunks.length);
      sendChunksToServer(audioChunks);
    }, 3000);
  };

  // --------------------------
  // ãƒãƒ£ãƒ³ã‚¯ã‚’ã‚µãƒ¼ãƒãƒ¼ã¸é€ä¿¡ (â€»å¤‰æ›´ã—ãªã„)
  // --------------------------
  const sendChunksToServer = async (chunks) => {
    try {
      setLoading(true);
      const formData = new FormData();
      chunks.forEach((blob, idx) => {
        formData.append('audios', blob, `recording_part_${idx}.webm`);
        console.log("é€ä¿¡ã™ã‚‹chunk:", `recording_part_${idx}`, blob.size);
      });

      console.log("ðŸ“¤ /transcribe-chunks ã¸POST");
      const res = await fetch(`${API_URL}/transcribe-chunks`, {
        method: 'POST',
        body: formData,
      });
      if (!res.ok) throw new Error(`ã‚µãƒ¼ãƒãƒ¼ã‚¨ãƒ©ãƒ¼: ${res.statusText}`);
      const data = await res.json();
      console.log("ðŸ§  /transcribe-chunks çµæžœ:", data);

      // ã‚µãƒ¼ãƒãƒ¼ã‹ã‚‰è¿”ã•ã‚ŒãŸè­°äº‹éŒ²ã‚’UIã«è¡¨ç¤º
      setNotes(data.formatted_transcript);
      setSummary(data.analysis);
      setMindmapData(data.mindmap);
      setTitle(data.title);

      setAudioChunks([]);
      localChunksRef.current = [];
    } catch (err) {
      console.error('é€šä¿¡ã‚¨ãƒ©ãƒ¼:', err);
      setNotes('é€šä¿¡ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸ');
    } finally {
      setLoading(false);
    }
  };

  // --------------------------
  // ãƒ†ã‚­ã‚¹ãƒˆãƒ¢ãƒ¼ãƒ‰: è­°äº‹éŒ²ç”Ÿæˆ
  // --------------------------
  const sendTextToServer = async () => {
    if (!inputTranscript.trim()) {
      alert("ãƒ†ã‚­ã‚¹ãƒˆãŒç©ºã§ã™ã€‚");
      return;
    }
    try {
      setLoading(true);
      const res = await fetch(`${API_URL}/transcribe-text`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ raw_text: inputTranscript }),
      });
      if (!res.ok) throw new Error(`ã‚µãƒ¼ãƒãƒ¼ã‚¨ãƒ©ãƒ¼: ${res.statusText}`);
      const data = await res.json();

      setNotes(data.formatted_transcript);
      setSummary(data.analysis);
      setMindmapData(data.mindmap);
      setTitle(data.title);
      setRecorded(true);
    } catch (err) {
      console.error('ãƒ†ã‚­ã‚¹ãƒˆãƒ¢ãƒ¼ãƒ‰å¤±æ•—:', err);
      setNotes('ãƒ†ã‚­ã‚¹ãƒˆè­°äº‹éŒ²ã®ç”Ÿæˆä¸­ã«ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸã€‚');
    } finally {
      setLoading(false);
    }
  };

  // --------------------------
  // ä¿å­˜ãƒ»å‰Šé™¤ãƒ»ã‚³ãƒ”ãƒ¼ãƒ»ãƒãƒ£ãƒƒãƒˆãªã©
  // --------------------------
  const saveToDatabase = async () => {
    try {
      const res = await fetch(`${API_URL}/save-minutes`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          formatted_transcript: notes,
          analysis: summary,
          mindmap: mindmapData,
          title: title,
        }),
      });
      if (!res.ok) {
        const errorText = await res.text();
        throw new Error(`ä¿å­˜ã«å¤±æ•—ã—ã¾ã—ãŸ: ${errorText}`);
      }
      const result = await res.json();
      if (result.status === 'success') {
        setSaveStatus('ä¿å­˜ãŒå®Œäº†ã—ã¾ã—ãŸï¼');
        setShowSaveModal(false);
        fetchSavedMinutes();
      } else {
        setSaveStatus('ä¿å­˜ã«å¤±æ•—ã—ã¾ã—ãŸ: ' + (result.detail || ''));
      }
    } catch (err) {
      console.error('ä¿å­˜å¤±æ•—:', err);
      setSaveStatus(err.message || 'ä¿å­˜ä¸­ã«ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸ');
    }
  };

  const deleteMinutes = async (id) => {
    if (!window.confirm("æœ¬å½“ã«å‰Šé™¤ã—ã¾ã™ã‹ï¼Ÿ")) return;
    try {
      const res = await fetch(`${API_URL}/delete-minutes/${id}`, {
        method: 'DELETE',
      });
      if (!res.ok) {
        const errorText = await res.text();
        throw new Error(`å‰Šé™¤ã«å¤±æ•—ã—ã¾ã—ãŸ: ${errorText}`);
      }
      const result = await res.json();
      if (result.status === 'success') {
        alert('å‰Šé™¤ã—ã¾ã—ãŸ');
        fetchSavedMinutes();
        if (selectedMinute && selectedMinute.id === id) {
          setSelectedMinute(null);
        }
      } else {
        alert('å‰Šé™¤ã«å¤±æ•—: ' + result.detail);
      }
    } catch (err) {
      console.error('å‰Šé™¤ã‚¨ãƒ©ãƒ¼:', err);
      alert('å‰Šé™¤ä¸­ã«ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸ');
    }
  };

  const handleCopyContent = async (content) => {
    try {
      await navigator.clipboard.writeText(content);
      alert('ã‚³ãƒ”ãƒ¼ã—ã¾ã—ãŸ!');
    } catch (err) {
      console.error('ã‚³ãƒ”ãƒ¼å¤±æ•—:', err);
      alert('ã‚³ãƒ”ãƒ¼ã«å¤±æ•—ã—ã¾ã—ãŸ');
    }
  };

  const sendChatMessage = async () => {
    if (!chatInput.trim()) return;
    const userMsg = { sender: 'user', text: chatInput };
    setChatMessages((prev) => [...prev, userMsg]);
    setChatInput("");
    try {
      const res = await fetch(`${API_URL}/chatbot`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: userMsg.text }),
      });
      if (!res.ok) {
        throw new Error(`ã‚µãƒ¼ãƒãƒ¼ã‚¨ãƒ©ãƒ¼: ${res.statusText}`);
      }
      const data = await res.json();
      const botMsg = { sender: 'bot', text: data.response };
      setChatMessages((prev) => [...prev, botMsg]);
    } catch (err) {
      console.error("ãƒãƒ£ãƒƒãƒˆbotã‚¨ãƒ©ãƒ¼:", err);
      const botMsg = { sender: 'bot', text: "ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸã€‚" };
      setChatMessages((prev) => [...prev, botMsg]);
    }
  };

  const closeSaveModal = () => {
    setSaveStatus(null);
  };
  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  // --------------------------
  // UIãƒ¬ãƒ³ãƒ€ãƒªãƒ³ã‚°
  // --------------------------
  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ minHeight: '100vh', background: 'linear-gradient(to bottom right, #f0f9ff, #cfe9ff, #eef6ff)' }}>
        <Button
          variant="contained"
          color="primary"
          onClick={toggleSidebar}
          sx={{
            position: 'absolute',
            left: 10,
            top: 20,
            borderRadius: '50%',
            width: 45,
            minWidth: 45,
            height: 45,
            fontSize: '1rem',
            zIndex: 2000,
          }}
        >
          {sidebarOpen ? 'â†' : 'â†’'}
        </Button>

        <AppBar position="sticky" sx={{ bgcolor: 'rgba(255,255,255,0.8)', backdropFilter: 'blur(8px)' }}>
          <Toolbar sx={{ justifyContent: 'center' }}>
            <Typography variant="h4" sx={{ color: 'primary.main', fontWeight: 'bold' }}>
              Conect AI
            </Typography>
          </Toolbar>
        </AppBar>

        <Drawer
          variant="persistent"
          anchor="left"
          open={sidebarOpen}
          sx={{
            '& .MuiDrawer-paper': {
              width: 240,
              borderRight: '1px solid #eee',
              bgcolor: 'rgba(255,255,255,0.85)',
              backdropFilter: 'blur(6px)',
              pt: 8,
            },
          }}
        >
          <Box sx={{ p: 2 }}>
            <Typography variant="subtitle1" sx={{ mb: 1, fontWeight: 'bold', color: '#1976d2' }}>
              ä¿å­˜ã—ãŸè­°äº‹éŒ²
            </Typography>
            <List>
              {savedMinutes.length > 0 ? (
                savedMinutes.map((minute) => (
                  <ListItemButton
                    key={minute.id}
                    onClick={() => {
                      setSelectedMinute(minute);
                      setShowSaveModal(false);
                    }}
                  >
                    <ListItemText primary={minute.title} />
                  </ListItemButton>
                ))
              ) : (
                <ListItemButton>
                  <ListItemText primary="ä¿å­˜ã•ã‚ŒãŸè­°äº‹éŒ²ã¯ã‚ã‚Šã¾ã›ã‚“" />
                </ListItemButton>
              )}
            </List>
          </Box>
        </Drawer>

        <Box sx={{ ml: sidebarOpen ? '240px' : 2, p: 3, pt: 10, transition: 'margin-left 0.3s ease' }}>
          <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, mb: 3 }}>
            <Button
              variant={mode === 'audio' ? 'contained' : 'outlined'}
              sx={{ borderRadius: 3, px: 4, py: 1, fontWeight: 'bold' }}
              onClick={() => setMode('audio')}
            >
              éŸ³å£°ãƒ¢ãƒ¼ãƒ‰
            </Button>
            <Button
              variant={mode === 'text' ? 'contained' : 'outlined'}
