import React, { useState, useEffect, useRef } from 'react';
import Tree from 'react-d3-tree';

/**
 * 【ポイント】
 * 1. 録音開始 → 録音停止（1回）で議事録生成が完結
 * 2. 10分ごとのチャンク分割を削除し、単純な1回録音方式に戻す
 * 3. 議事録削除機能（delete-minutes）を保持
 * 4. テキストモードは従来通り
 */

function App() {
  // ローカル用APIエンドポイント
  const API_URL = "http://localhost:8000";

  // --------------------------
  // ステート定義
  // --------------------------
  const [notes, setNotes] = useState('ここに文字起こしが表示されます');
  const [summary, setSummary] = useState('ここに議事録・改善案が表示されます');
  const [mindmapData, setMindmapData] = useState(null);
  const [title, setTitle] = useState('');

  const [recording, setRecording] = useState(false);
  const [recorded, setRecorded] = useState(false);
  const [showSaveModal, setShowSaveModal] = useState(false);

  const [loading, setLoading] = useState(false);
  const [saveStatus, setSaveStatus] = useState(null);

  const [savedMinutes, setSavedMinutes] = useState([]);
  const [selectedMinute, setSelectedMinute] = useState(null);

  const [sidebarOpen, setSidebarOpen] = useState(true);

  // 音声録音関連
  const mediaRecorderRef = useRef(null);
  const [audioChunks, setAudioChunks] = useState([]); 
  const [recordingDuration, setRecordingDuration] = useState(0);
  const durationIntervalRef = useRef(null);

  // テキストモード用
  const [mode, setMode] = useState("audio");
  const [inputTranscript, setInputTranscript] = useState("");

  // --------------------------
  // 1. APIから保存済み議事録を取得
  // --------------------------
  const fetchSavedMinutes = async () => {
    try {
      const res = await fetch(`${API_URL}/get-minutes`);
      if (!res.ok) throw new Error('保存された議事録の取得に失敗しました');
      const data = await res.json();
      // 直近のものが上になるようソート
      const sorted = data.minutes.sort((a, b) => b.id - a.id);
      setSavedMinutes(sorted);
    } catch (error) {
      console.error('Error fetching saved minutes:', error);
    }
  };

  useEffect(() => {
    fetchSavedMinutes();
  }, []);

  // --------------------------
  // 2. 音声モード: 会議開始
  // --------------------------
  const startRecording = async () => {
    setRecording(true);
    setRecorded(false);
    setNotes('録音が開始されました');
    setSummary('会議中...');
    setAudioChunks([]); // 前回の録音データをクリア
    setRecordingDuration(0);

    try {
      // マイクアクセス
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);

      const localChunks = [];
      // データがまとまるたびに呼ばれる
      mediaRecorder.addEventListener("dataavailable", event => {
        if (event.data.size > 0) {
          localChunks.push(event.data);
        }
      });

      // stop() されたら Blob をまとめる
      mediaRecorder.addEventListener("stop", () => {
        const blob = new Blob(localChunks, { type: 'audio/mp3' });
        // Stateに格納
        setAudioChunks([blob]);
      });

      // 実際に録音スタート
      mediaRecorderRef.current = mediaRecorder;
      mediaRecorder.start();

      // 経過時間カウント
      const timer = setInterval(() => {
        setRecordingDuration(prev => prev + 1);
      }, 1000);
      durationIntervalRef.current = timer;

    } catch (err) {
      console.error('録音の開始に失敗:', err);
      setNotes('マイクが許可されていません');
      setRecording(false);
    }
  };

  // --------------------------
  // 3. 音声モード: 会議終了 → 議事録生成
  // --------------------------
  const stopRecording = async () => {
    setRecording(false);
    setRecorded(true);

    // タイマー停止
    if (durationIntervalRef.current) {
      clearInterval(durationIntervalRef.current);
      durationIntervalRef.current = null;
    }

    // MediaRecorder停止
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
      mediaRecorderRef.current.stop();
    }

    setNotes('録音が停止しました。文字起こしを準備中...');

    // 注意: Blobが確定するのは "stop()" イベント発火後なので、
    // すこし遅れて audioChunks が更新されます。
    // そのため、setTimeoutで少し待ってからサーバー送信を呼び出すなど工夫する。
    setTimeout(() => {
      if (!audioChunks || audioChunks.length === 0) {
        console.error('音声がありません');
        return;
      }
      sendChunksToServer();
    }, 500);
  };

  // --------------------------
  // 4. 音声データを送信し、議事録生成
  // --------------------------
  const sendChunksToServer = async () => {
    try {
      setLoading(true);
      const formData = new FormData();
      audioChunks.forEach((blob, index) => {
        formData.append('audios', blob, `recording_part_${index}.mp3`);
      });

      const res = await fetch(`${API_URL}/transcribe-chunks`, {
        method: 'POST',
        body: formData,
      });
      if (!res.ok) throw new Error(`サーバーエラー: ${res.statusText}`);

      const data = await res.json();
      setNotes(data.formatted_transcript);
      setSummary(data.analysis);
      setMindmapData(data.mindmap);
      setTitle(data.title);

      // 使い終わったらクリア
      setAudioChunks([]);
    } catch (error) {
      console.error('通信エラー:', error);
      setNotes('通信エラーが発生しました（ファイルサイズ制限の可能性あり）');
    } finally {
      setLoading(false);
    }
  };

  // --------------------------
  // 5. テキストモード: テキスト送信 → 議事録生成
  // --------------------------
  const sendTextToServer = async () => {
    if (!inputTranscript.trim()) {
      alert("テキストが空です。");
      return;
    }
    try {
      setLoading(true);
      const res = await fetch(`${API_URL}/transcribe-text`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ raw_text: inputTranscript }),
      });
      if (!res.ok) throw new Error(`サーバーエラー: ${res.statusText}`);

      const data = await res.json();
      setNotes(data.formatted_transcript);
      setSummary(data.analysis);
      setMindmapData(data.mindmap);
      setTitle(data.title);

      setRecorded(true);
    } catch (err) {
      console.error('通信エラー:', err);
      setNotes('テキスト議事録の生成中にエラーが発生しました。');
    } finally {
      setLoading(false);
    }
  };

  // --------------------------
  // 6. 議事録保存
  // --------------------------
  const saveToDatabase = async () => {
    try {
      const res = await fetch(`${API_URL}/save-minutes`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          formatted_transcript: notes,
          analysis: summary,
          mindmap: mindmapData,
          title: title,
        }),
      });
      if (!res.ok) {
        const errorText = await res.text();
        throw new Error(`保存に失敗しました: ${errorText}`);
      }
      const result = await res.json();
      if (result.status === 'success') {
        setSaveStatus('保存が完了しました！');
        setShowSaveModal(false);
        fetchSavedMinutes();
      } else {
        setSaveStatus('保存に失敗しました: ' + (result.detail || ''));
      }
    } catch (err) {
      console.error(err);
      setSaveStatus(err.message || '保存中にエラーが発生しました');
    }
  };

  // --------------------------
  // 7. 議事録削除
  // --------------------------
  const deleteMinutes = async (id) => {
    if (!window.confirm("本当に削除しますか？")) return;
    try {
      const res = await fetch(`${API_URL}/delete-minutes/${id}`, {
        method: 'DELETE',
      });
      if (!res.ok) {
        const errorText = await res.text();
        throw new Error(`削除に失敗しました: ${errorText}`);
      }
      const result = await res.json();
      if (result.status === 'success') {
        alert('削除しました');
        // 一覧を再取得
        fetchSavedMinutes();
        if (selectedMinute && selectedMinute.id === id) {
          setSelectedMinute(null);
        }
      } else {
        alert('削除に失敗: ' + result.detail);
      }
    } catch (err) {
      console.error(err);
      alert('削除中にエラーが発生しました');
    }
  };

  // モーダル閉じる
  const closeSaveModal = () => {
    setSaveStatus(null);
  };

  // サイドバーの開閉
  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <div style={styles.appContainer}>
      {/* キーアニメーション定義 */}
      <style>{`
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }
        @keyframes slideDown {
          from { transform: translateY(-20px); opacity: 0; }
          to { transform: translateY(0); opacity: 1; }
        }
        @keyframes popIn {
          from { transform: scale(0.9); opacity: 0; }
          to { transform: scale(1); opacity: 1; }
        }
        @keyframes futuristicGlow {
          0% { box-shadow: 0 0 8px rgba(0,255,255,0.4); }
          50% { box-shadow: 0 0 20px rgba(0,255,255,0.8); }
          100% { box-shadow: 0 0 8px rgba(0,255,255,0.4); }
        }
      `}</style>

      {/* ヘッダー */}
      <header style={styles.header}>
        <div style={styles.headerLeft}>
          <button style={styles.toggleButton} onClick={toggleSidebar}>
            {sidebarOpen ? '←' : '→'}
          </button>
          <h1 style={styles.logo}>Conect AI</h1>
        </div>
        <div style={styles.headerRight}>{/* ナビゲーションなど */}</div>
      </header>

      {/* メインレイアウト */}
      <div style={styles.contentWrapper}>
        {/* サイドバー */}
        {sidebarOpen && (
          <aside style={styles.sidebar}>
            <h2 style={styles.sidebarTitle}>保存した議事録</h2>
            {savedMinutes.length > 0 ? (
              savedMinutes.map((minute) => (
                <div
                  key={minute.id}
                  style={styles.sidebarItem}
                  onClick={() => {
                    setSelectedMinute(minute);
                    setShowSaveModal(false);
                  }}
                >
                  {minute.title}
                </div>
              ))
            ) : (
              <p style={styles.sidebarText}>保存された議事録はありません</p>
            )}
          </aside>
        )}

        {/* メインコンテンツ */}
        <main style={{ ...styles.mainContent, marginLeft: sidebarOpen ? 300 : 20 }}>
          {selectedMinute ? (
            // 保存済み議事録詳細表示
            <div style={styles.detailContainer}>
              <button style={styles.backButton} onClick={() => setSelectedMinute(null)}>
                戻る
              </button>
              <h2 style={styles.detailTitle}>{selectedMinute.title}</h2>
              <div style={styles.card}>
                <h3 style={styles.cardTitle}>整形された文字起こし</h3>
                <p style={styles.cardContent}>{selectedMinute.formatted_transcript}</p>
              </div>
              <div style={styles.card}>
                <h3 style={styles.cardTitle}>議事録・改善案</h3>
                <p style={styles.cardContent}>{selectedMinute.analysis}</p>
              </div>
              {selectedMinute.mindmap && (
                <div style={styles.mindmapContainer}>
                  <Tree
                    data={selectedMinute.mindmap}
                    orientation="vertical"
                    translate={{ x: 400, y: 50 }}
                    pathFunc="diagonal"
                    separation={{ siblings: 1.5, nonSiblings: 2 }}
                    nodeSize={{ x: 300, y: 100 }}
                  />
                </div>
              )}
              {/* 削除ボタン */}
              <button
                style={{ ...styles.backButton, backgroundColor: 'red' }}
                onClick={() => deleteMinutes(selectedMinute.id)}
              >
                この議事録を削除
              </button>
            </div>
          ) : (
            // 新規録音＆生成結果表示
            <div style={styles.recorderContainer}>
              <div style={styles.descriptionBox}>
                <p style={styles.descriptionText}>
                  このアプリでは、会議の録音、文字起こし、議事録・改善案生成、マインドマップ作成が可能です。<br />
                  「会議開始」ボタンを押すと録音が開始され、「会議終了」ボタンで録音を停止すると自動で議事録が生成されます。<br />
                  また、テキストモードを選択し、オンライン会議の文字起こしを貼り付けても議事録作成が可能です。<br />
                </p>
              </div>

              {/* モード切り替え */}
              <div style={{ marginBottom: 20 }}>
                <label style={{ marginRight: 10 }}>
                  <input
                    type="radio"
                    name="mode"
                    value="audio"
                    checked={mode === "audio"}
                    onChange={() => setMode("audio")}
                  />
                  音声モード
                </label>
                <label>
                  <input
                    type="radio"
                    name="mode"
                    value="text"
                    checked={mode === "text"}
                    onChange={() => setMode("text")}
                  />
                  テキストモード
                </label>
              </div>

              {mode === "audio" ? (
                <>
                  {!recording ? (
                    <button style={styles.actionButton} onClick={startRecording}>
                      会議開始
                    </button>
                  ) : (
                    <button style={styles.actionButton} onClick={stopRecording}>
                      会議終了
                    </button>
                  )}

                  {recording && (
                    <p style={{ marginTop: '10px', animation: 'fadeIn 0.5s ease' }}>
                      録音中... {Math.floor(recordingDuration / 60)}分 {recordingDuration % 60}秒
                    </p>
                  )}
                </>
              ) : (
                <>
                  <label>オンライン会議の文字起こしを貼り付け:</label>
                  <textarea
                    style={styles.editableTextarea}
                    value={inputTranscript}
                    onChange={(e) => setInputTranscript(e.target.value)}
                    placeholder="ここにテキストを貼り付けてください"
                  />
                  <button style={styles.actionButton} onClick={sendTextToServer}>
                    議事録生成
                  </button>
                </>
              )}

              {/* 生成結果表示部分 */}
              {(recorded || (mode === "text" && notes)) && notes && (
                <div style={styles.generatedContainer}>
                  <div style={styles.generatedContent}>
                    <h2 style={styles.generatedTitle}>生成された議事録（編集可能）</h2>
                    <div style={{ marginBottom: '10px' }}>
                      <label>タイトル: </label>
                      <input
                        type="text"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        style={styles.editableTitle}
                      />
                    </div>
                    <div style={{ marginBottom: '10px' }}>
                      <label>文字起こし: </label>
                      <textarea
                        value={notes}
                        onChange={(e) => setNotes(e.target.value)}
                        style={styles.editableTextarea}
                      />
                    </div>
                    <div style={{ marginBottom: '10px' }}>
                      <label>議事録・改善案: </label>
                      <textarea
                        value={summary}
                        onChange={(e) => setSummary(e.target.value)}
                        style={styles.editableTextarea}
                      />
                    </div>
                    {mindmapData && (
                      <div style={styles.mindmapContainer}>
                        <Tree
                          data={mindmapData}
                          orientation="vertical"
                          translate={{ x: 400, y: 50 }}
                          pathFunc="diagonal"
                          separation={{ siblings: 1.5, nonSiblings: 2 }}
                          nodeSize={{ x: 300, y: 100 }}
                        />
                      </div>
                    )}
                  </div>
                  {/* 保存ボタン */}
                  <button style={styles.actionButton} onClick={() => setShowSaveModal(true)}>
                    確認画面へ
                  </button>
                </div>
              )}

              {/* 議事録保存用モーダル */}
              {showSaveModal && (
                <div style={styles.modalOverlay}>
                  <div style={styles.modalContent}>
                    <h2 style={styles.modalTitle}>この内容で保存します</h2>
                    <div style={styles.modalSection}>
                      <h3 style={styles.modalSubTitle}>タイトル</h3>
                      <p style={styles.modalText}>{title}</p>
                    </div>
                    <div style={styles.modalSection}>
                      <h3 style={styles.modalSubTitle}>文字起こし</h3>
                      <p style={styles.modalText}>{notes}</p>
                    </div>
                    <div style={styles.modalSection}>
                      <h3 style={styles.modalSubTitle}>議事録・改善案</h3>
                      <p style={styles.modalText}>{summary}</p>
                    </div>
                    {mindmapData && (
                      <div style={styles.modalSection}>
                        <h3 style={styles.modalSubTitle}>マインドマップ</h3>
                        <div style={styles.mindmapModalContainer}>
                          <Tree
                            data={mindmapData}
                            orientation="vertical"
                            translate={{ x: 350, y: 50 }}
                            pathFunc="diagonal"
                            separation={{ siblings: 1.5, nonSiblings: 2 }}
                            nodeSize={{ x: 300, y: 100 }}
                          />
                        </div>
                      </div>
                    )}
                    <button style={styles.modalActionButton} onClick={saveToDatabase}>
                      この内容で保存する
                    </button>
                    <button style={styles.modalCloseButton} onClick={() => setShowSaveModal(false)}>
                      閉じる
                    </button>
                  </div>
                </div>
              )}
            </div>
          )}
        </main>
      </div>

      {/* ローディングモーダル */}
      {loading && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <p style={styles.modalText}>議事録を生成中です...お待ちください</p>
          </div>
        </div>
      )}

      {/* 保存結果モーダル */}
      {saveStatus && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <p style={styles.modalText}>{saveStatus}</p>
            <button style={styles.modalCloseButton} onClick={closeSaveModal}>
              閉じる
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

