  /**
   * ãƒ–ãƒ©ã‚¦ã‚¶ãŒã‚µãƒãƒ¼ãƒˆã™ã‚‹éŒ²éŸ³mimeTypeã‚’é †ã«æŽ¢ã™
   */
  function getSupportedMimeType() {
    const possibleTypes = [
      "audio/webm;codecs=opus",
      "audio/ogg;codecs=opus",
      "audio/webm",
      "audio/ogg"
    ];
    for (const t of possibleTypes) {
      if (MediaRecorder.isTypeSupported(t)) {
        return t;
      }
    }
    return "";
  }

  // ====================================================
  // éŒ²éŸ³é–‹å§‹
  // ====================================================
  const startRecording = async () => {
    setRecording(true);
    setRecorded(false);
    setNotes('éŒ²éŸ³ãŒé–‹å§‹ã•ã‚Œã¾ã—ãŸ');
    setSummary('ä¼šè­°ä¸­...');
    setAudioChunks([]);
    localChunksRef.current = [];

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaStreamRef.current = stream;
      const mimeType = getSupportedMimeType() || "audio/webm";
      const mediaRecorder = new MediaRecorder(stream, { mimeType });

      // dataavailable ã‚¤ãƒ™ãƒ³ãƒˆ â†’ å°ãƒãƒ£ãƒ³ã‚¯ã‚’ä¸€æ™‚ä¿ç®¡
      mediaRecorder.addEventListener("dataavailable", (e) => {
        if (e.data.size > 0) {
          localChunksRef.current.push(e.data);
          console.log("dataavailable chunk size:", e.data.size);
        }
      });

      // stop ã‚¤ãƒ™ãƒ³ãƒˆ â†’ 1ç§’å¾Œã« localChunksRef ã‹ã‚‰1ã¤ã®Blobã«ã—ã¦ audioChunks ã¸è¿½åŠ 
      mediaRecorder.addEventListener("stop", () => {
        setTimeout(() => {
          if (localChunksRef.current.length > 0) {
            const blob = new Blob(localChunksRef.current, { type: mimeType });
            console.log("â˜… stop: Blob size =", blob.size);
            setAudioChunks((prev) => [...prev, blob]);
            localChunksRef.current = [];
          }
          // ã‚‚ã—éŒ²éŸ³ãŒç¶™ç¶šä¸­ãªã‚‰å†åº¦ start() ã™ã‚‹ï¼ˆ10åˆ†åˆ†å‰²ç”¨ï¼‰
          if (recording) {
            // ã¾ã æœ€çµ‚stopã§ã¯ãªã„ â†’ éŒ²éŸ³å†é–‹
            mediaRecorder.start();
            console.log("â˜… chunk stopâ†’start(10åˆ†åŒºåˆ‡ã‚Š)");
          }
        }, 1000);
      });

      mediaRecorderRef.current = mediaRecorder;
      mediaRecorder.start();
      console.log("ðŸŽ™ï¸ éŒ²éŸ³é–‹å§‹");

      // 10åˆ†ã”ã¨ã« stop() (=> stopã‚¤ãƒ™ãƒ³ãƒˆç™ºç«å¾Œã€1ç§’å¾Œã« blobåŒ– & audioChunksã¸)
      chunkIntervalRef.current = setInterval(() => {
        if (mediaRecorder.state === "recording") {
          console.log("=== 10åˆ†çµŒéŽ: ã„ã£ãŸã‚“ stop() ã—ã¾ã™ ===");
          mediaRecorder.stop();
        }
      }, 600000); // 10åˆ† = 600000 ms

    } catch (err) {
      console.error('éŒ²éŸ³é–‹å§‹å¤±æ•—:', err);
      setNotes('éŒ²éŸ³é–‹å§‹ã«å¤±æ•—ã—ã¾ã—ãŸ: ' + err.message);
      setRecording(false);
    }
  };

  // ====================================================
  // éŒ²éŸ³åœæ­¢ (ãƒ¦ãƒ¼ã‚¶ãƒ¼æ“ä½œ)
  // ====================================================
  const stopRecording = async () => {
    console.log("ðŸ›‘ éŒ²éŸ³åœæ­¢ãƒœã‚¿ãƒ³æŠ¼ä¸‹");
    setRecording(false);

    // 10åˆ†åŒºåˆ‡ã‚Š Intervalã‚’ã‚¯ãƒªã‚¢
    if (chunkIntervalRef.current) {
      clearInterval(chunkIntervalRef.current);
      chunkIntervalRef.current = null;
    }

    if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
      mediaRecorderRef.current.stop();
    }
    if (mediaStreamRef.current) {
      mediaStreamRef.current.getTracks().forEach((track) => track.stop());
    }

    setNotes("éŒ²éŸ³ãŒåœæ­¢ã—ã¾ã—ãŸã€‚æ–‡å­—èµ·ã“ã—ã‚’æº–å‚™ä¸­...");
    setRecorded(true);

    // éŒ²éŸ³åœæ­¢å¾Œã€1ç§’å¾…ã£ã¦ã€Œæœ€å¾Œã®ãƒãƒ£ãƒ³ã‚¯ã€ãŒ BlobåŒ– & audioChunksã«è¿½åŠ ã•ã‚Œã‚‹
    // ã•ã‚‰ã« +1.5ç§’ã»ã©å¾…ã£ã¦ã‹ã‚‰ sendChunksToServer(audioChunks)
    setTimeout(() => {
      if (!audioChunks || audioChunks.length === 0) {
        console.warn("âš ï¸ ãƒãƒ£ãƒ³ã‚¯ãŒã‚ã‚Šã¾ã›ã‚“");
