import React, { useState, useEffect, useRef } from 'react';
import Tree from 'react-d3-tree';

// ======== MUI„Ç≥„É≥„Éù„Éº„Éç„É≥„Éà & „Éï„É¨„Éº„É†„ÉØ„Éº„ÇØ =========
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Drawer,
  List,
  ListItemButton,
  ListItemText,
  Box,
  Paper,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  CircularProgress,
  IconButton
} from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

// ======== Framer Motion for Animations =========
import { motion, AnimatePresence } from 'framer-motion';

// ======== „Ç¢„Ç§„Ç≥„É≥ =========
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import CloseIcon from '@mui/icons-material/Close';

/**
 * „ÉÜ„Éº„Éû„Çí‰ΩúÊàêÔºàÈÉΩ‰ºöÁöÑ„Å™„Éñ„É´„Éº√ó„Éõ„ÉØ„Ç§„ÉàÔºã„ÇÑ„ÇÑËøëÊú™Êù•ÊÑüÔºâ
 */
const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2', // MaterialUIÊó¢ÂÆö„ÅÆ„Éñ„É´„Éº
    },
    secondary: {
      main: '#00f2fe', // ËøëÊú™Êù•„ÅΩ„ÅÑ„É©„Ç§„Éà„Éñ„É´„Éº
    },
  },
  typography: {
    fontFamily: [
      'Montserrat',
      'Playfair Display',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
    ].join(','),
  },
});

/**
 * „Ç¢„Éã„É°„Éº„Ç∑„Éß„É≥Áî®„ÅÆ„Éê„É™„Ç¢„É≥„Éà‰æã
 */
const fadeInVariants = {
  hidden: { opacity: 0, y: -10 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.5 } },
};
const popInVariants = {
  hidden: { opacity: 0, scale: 0.9 },
  visible: { opacity: 1, scale: 1, transition: { duration: 0.4 } },
};

function App() {
  const API_URL = "http://localhost:8000";

  // --------------------------
  // „Çπ„ÉÜ„Éº„ÉàÈ°ûÔºàË≠∞‰∫ãÈå≤‰ΩúÊàêÈÉ®ÂàÜÔºâ
  // --------------------------
  const [notes, setNotes] = useState('„Åì„Åì„Å´ÊñáÂ≠óËµ∑„Åì„Åó„ÅåË°®Á§∫„Åï„Çå„Åæ„Åô');
  const [summary, setSummary] = useState('„Åì„Åì„Å´Ë≠∞‰∫ãÈå≤„ÉªÊîπÂñÑÊ°à„ÅåË°®Á§∫„Åï„Çå„Åæ„Åô');
  const [mindmapData, setMindmapData] = useState(null);
  const [title, setTitle] = useState('');

  const [recording, setRecording] = useState(false);
  const [recorded, setRecorded] = useState(false);
  const [showSaveModal, setShowSaveModal] = useState(false);
  const [loading, setLoading] = useState(false);
  const [saveStatus, setSaveStatus] = useState(null);

  const [savedMinutes, setSavedMinutes] = useState([]);
  const [selectedMinute, setSelectedMinute] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  // Èå≤Èü≥Èñ¢ÈÄ£
  const mediaRecorderRef = useRef(null);
  const [audioChunks, setAudioChunks] = useState([]);
  const [recordingTimerId, setRecordingTimerId] = useState(null);
  const [recordingDuration, setRecordingDuration] = useState(0);
  const durationIntervalRef = useRef(null);
  const mediaStreamRef = useRef(null); // ËøΩÂä†ÔºöMediaStream‰øùÊåÅÁî®

  // --------------------------
  // „É¢„Éº„Éâ: audio | text | chat
  // --------------------------
  const [mode, setMode] = useState("audio");
  const [inputTranscript, setInputTranscript] = useState("");

  // --------------------------
  // „ÉÅ„É£„ÉÉ„Éà„É¢„Éº„ÉâÁî®
  // --------------------------
  const [chatMessages, setChatMessages] = useState([]); // { sender:'user'|'bot', text:''}[]
  const [chatInput, setChatInput] = useState("");

  // --------------------------
  // 1) ‰øùÂ≠òÊ∏à„ÅøË≠∞‰∫ãÈå≤„ÇíÂèñÂæó
  // --------------------------
  const fetchSavedMinutes = async () => {
    try {
      const res = await fetch(`${API_URL}/get-minutes`);
      if (!res.ok) throw new Error('‰øùÂ≠ò„Åï„Çå„ÅüË≠∞‰∫ãÈå≤„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
      const data = await res.json();
      const sorted = data.minutes.sort((a, b) => b.id - a.id);
      setSavedMinutes(sorted);
    } catch (error) {
      console.error('Error fetching saved minutes:', error);
    }
  };

  useEffect(() => {
    fetchSavedMinutes();
  }, []);

  /**
   * „Éñ„É©„Ç¶„Ç∂„Åå„Çµ„Éù„Éº„Éà„Åô„ÇãÈå≤Èü≥mimeType„ÇíÈ†Ü„Å´Êé¢„Åô
   */
  function getSupportedMimeType() {
    const possibleTypes = [
      "audio/webm;codecs=opus",
      "audio/ogg;codecs=opus",
      "audio/webm",
      "audio/ogg"
    ];
    for (const t of possibleTypes) {
      if (MediaRecorder.isTypeSupported(t)) {
        return t;
      }
    }
    return "";
  }

  // ====================================================
  // Èå≤Èü≥ÈñãÂßãÔºà10ÂàÜ„Åî„Å®„Å´„ÉÅ„É£„É≥„ÇØÂàÜÂâ≤Ôºâ
  // ====================================================
  const startRecording = async () => {
    setRecording(true);
    setRecorded(false);
    setAudioChunks([]);
    setRecordingDuration(0);
    setNotes('Èå≤Èü≥„ÅåÈñãÂßã„Åï„Çå„Åæ„Åó„Åü');
    setSummary('‰ºöË≠∞‰∏≠...');

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaStreamRef.current = stream;
      const mimeType = getSupportedMimeType();
      if (!mimeType) {
        throw new Error("„Éñ„É©„Ç¶„Ç∂„Åå„Çµ„Éù„Éº„Éà„Åô„ÇãÈå≤Èü≥„Éï„Ç©„Éº„Éû„ÉÉ„Éà„Åå„ÅÇ„Çä„Åæ„Åõ„Çì„ÄÇ");
      }

      // MediaRecorder „Çí‰ΩúÊàê„Åô„Çã„Éò„É´„Éë„ÉºÈñ¢Êï∞
      const createRecorder = () => {
        const mediaRecorder = new MediaRecorder(stream, { mimeType });
        let localChunks = [];

        mediaRecorder.addEventListener("dataavailable", (e) => {
          if (e.data.size > 0) {
            localChunks.push(e.data);
          }
        });

        mediaRecorder.addEventListener("stop", () => {
          // ÂèñÂæó„Åó„Åü„ÉÅ„É£„É≥„ÇØ„ÇíÁµêÂêà„Åó„Å¶1„Éï„Ç°„Ç§„É´„Å´„Åô„Çã
          const blob = new Blob(localChunks, { type: mimeType });
          // „Éï„Ç°„Ç§„É´Âêç„Å´‰∏ÄÊÑèÊÄß„ÇíÊåÅ„Åü„Åõ„Çã
          const file = new File([blob], `recording_part_${Date.now()}.webm`, { type: mimeType });
          setAudioChunks(prev => [...prev, file]);
          localChunks = [];

          // Èå≤Èü≥‰∏≠Ôºàrecording „Åå trueÔºâ„Å™„Çâ„ÄÅÊ¨°„ÅÆÈå≤Èü≥„ÇíÈñãÂßã„Åô„Çã
          if (recording) {
            const nextRecorder = createRecorder();
            mediaRecorderRef.current = nextRecorder;
            nextRecorder.start();
          }
        });

        return mediaRecorder;
      };

      // ÊúÄÂàù„ÅÆ Recorder „ÇíÈñãÂßã
      const firstRecorder = createRecorder();
      mediaRecorderRef.current = firstRecorder;
      firstRecorder.start();

      // 10ÂàÜÔºà600,000msÔºâ„Åî„Å®„Å´ÁèæÂú®„ÅÆ Recorder „ÇíÂÅúÊ≠¢„Åó„ÄÅÊñ∞„Åü„Å´ÈñãÂßã„Åï„Åõ„Çã
      const chunkTimer = setInterval(() => {
        if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
          mediaRecorderRef.current.stop();
        }
      }, 600000);
      setRecordingTimerId(chunkTimer);

      // ÁµåÈÅéÊôÇÈñì„Ç´„Ç¶„É≥„Çø
      const durationTimer = setInterval(() => {
        setRecordingDuration(prev => prev + 1);
      }, 1000);
      durationIntervalRef.current = durationTimer;

    } catch (err) {
      console.error('Èå≤Èü≥ÈñãÂßãÂ§±Êïó:', err);
      setNotes('Èå≤Èü≥ÈñãÂßã„Å´Â§±Êïó„Åó„Åæ„Åó„Åü: ' + err.message);
      setRecording(false);
    }
  };

  // ====================================================
  // Èå≤Èü≥ÂÅúÊ≠¢ & Ëá™ÂãïË≠∞‰∫ãÈå≤‰ΩúÊàê„Å∏„ÅÆÈÄÅ‰ø°
  // ====================================================
  const stopRecording = async () => {
    setRecording(false);
    setRecorded(true);

    if (recordingTimerId) {
      clearInterval(recordingTimerId);
      setRecordingTimerId(null);
    }
    if (durationIntervalRef.current) {
      clearInterval(durationIntervalRef.current);
      durationIntervalRef.current = null;
    }

    if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
      mediaRecorderRef.current.stop();
    }

    // MediaStream „ÅÆ„Éà„É©„ÉÉ„ÇØ„ÇíÂÅúÊ≠¢Ôºà„É™„ÇΩ„Éº„ÇπËß£ÊîæÔºâ
    if (mediaStreamRef.current) {
      mediaStreamRef.current.getTracks().forEach((track) => track.stop());
    }

    setNotes("Èå≤Èü≥„ÅåÂÅúÊ≠¢„Åó„Åæ„Åó„Åü„ÄÇÊñáÂ≠óËµ∑„Åì„Åó„ÇíÊ∫ñÂÇô‰∏≠...");

    // ÊúÄÂæå„ÅÆ„ÉÅ„É£„É≥„ÇØ„Åå audioChunks „Å´ÂèçÊò†„Åï„Çå„Çã„Åæ„ÅßÂæÖ„Å£„Å¶„Åã„ÇâÈÄÅ‰ø°
    const waitChunks = setInterval(() => {
      if (audioChunks.length > 0) {
        clearInterval(waitChunks);
        console.log("üîÅ „ÉÅ„É£„É≥„ÇØÈÄÅ‰ø°ÈñãÂßã:", audioChunks);
        sendChunksToServer();
      }
    }, 300);

    // ‰øùÈô∫„Å®„Åó„Å¶5Áßí‰ª•‰∏ä„Åã„Åã„Å£„Åü„ÇâÂº∑Âà∂ÈÄÅ‰ø°Ôºà„Ç®„É©„ÉºÈò≤Ê≠¢Ôºâ
    setTimeout(() => {
      if (audioChunks.length === 0) {
        console.warn("‚è∞ „ÉÅ„É£„É≥„ÇØ„Åå‰ΩúÊàê„Åï„Çå„Å™„ÅÑ„Åü„ÇÅ„ÄÅÂº∑Âà∂ÁöÑ„Å´ÈÄÅ‰ø°");
        sendChunksToServer();
      }
    }, 5000);
  };

  // ====================================================
  // Ë§áÊï∞„ÉÅ„É£„É≥„ÇØÈÄÅ‰ø° ‚Üí Ë≠∞‰∫ãÈå≤ÁîüÊàê
  // ====================================================
  const sendChunksToServer = async () => {
    if (!audioChunks || audioChunks.length === 0) {
      console.warn('Èü≥Â£∞„ÉÅ„É£„É≥„ÇØ„Åå„ÅÇ„Çä„Åæ„Åõ„Çì');
      return;
    }
    try {
      setLoading(true);
      const formData = new FormData();
      audioChunks.forEach((file, index) => {
        formData.append('audios', file, file.name || `recording_part_${index}.webm`);
      });

      const res = await fetch(`${API_URL}/transcribe-chunks`, {
        method: 'POST',
        body: formData,
      });
      if (!res.ok) throw new Error(`„Çµ„Éº„Éê„Éº„Ç®„É©„Éº: ${res.statusText}`);

      const data = await res.json();
      setNotes(data.formatted_transcript);
      setSummary(data.analysis);
