import React, { useState, useEffect, useRef } from 'react';
import Tree from 'react-d3-tree';

// ======== MUI & „Åù„ÅÆ‰ªñ ========
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Drawer,
  List,
  ListItemButton,
  ListItemText,
  Box,
  Paper,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  CircularProgress,
  IconButton
} from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { motion, AnimatePresence } from 'framer-motion';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';

/** „ÉÜ„Éº„ÉûË®≠ÂÆö */
const theme = createTheme({
  palette: {
    primary: { main: '#1976d2' },
    secondary: { main: '#00f2fe' },
  },
  typography: {
    fontFamily: [
      'Montserrat',
      'Playfair Display',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
    ].join(','),
  },
});

// „Ç¢„Éã„É°„Éº„Ç∑„Éß„É≥Áî®„Éê„É™„Ç¢„É≥„Éà
const fadeInVariants = {
  hidden: { opacity: 0, y: -10 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.5 } },
};

/**
 * Êûù„ÅÆ„Ç´„Çπ„Çø„É†ÊèèÁîªÁî®„Ç≥„É≥„Éù„Éº„Éç„É≥„Éà
 * „Åì„Åì„Åß linkDatum.source.depth „Å™„Å©„Çí‰Ωø„Å£„Å¶Ëâ≤„ÇíÂàÜÂ≤ê„Åï„Åõ„Çã
 */
const colorPalette = ['#cc00cc', '#ff4500', '#00aaff', '#ff00ff', '#ff8c00', '#228b22'];
function CustomColoredLink({ linkDatum, orientation }) {
  // ÈöéÂ±§ÔºàdepthÔºâ„Å´Âøú„Åò„Å¶Ëâ≤„ÇíÂàá„ÇäÊõø„Åà„Åü„Çä„ÄÅ„ÅÇ„Çã„ÅÑ„ÅØÂ≠ê„Éé„Éº„Éâ„ÅÆÊÉÖÂ†±„ÅßÂàÜÂ≤ê„Åï„Åõ„Å¶„ÇÇOK
  const strokeColor = colorPalette[linkDatum.source.depth % colorPalette.length];

  // "diagonal" „Éë„ÇπË®àÁÆóÔºàÊ®™Âêë„Åç„É¨„Ç§„Ç¢„Ç¶„ÉàÁî®Ôºâ
  // „Åì„Åì„Åß„ÅØÁ∞°ÊòìÁöÑ„Å´„Éô„Ç∏„ÇßÊõ≤Á∑ö„ÅßÂ∑¶Âè≥„Å´‰º∏„Å≥„ÇãÂØæËßíÁ∑ö„Çí‰ΩúÊàê
  const { source, target } = linkDatum;
  // x=Ê®™Ëª∏, y=Á∏¶Ëª∏„Åß„ÄÅÊ®™Âêë„Åç„ÅÆÂ†¥Âêà„ÅØ X „ÅåÂ∑¶Âè≥, Y „Åå‰∏ä‰∏ã
  const path = `M${source.x},${source.y}
                C${(source.x + target.x) / 2},${source.y},
                ${(source.x + target.x) / 2},${target.y},
                ${target.x},${target.y}`;

  return (
    <path
      d={path}
      fill="none"
      stroke={strokeColor}
      strokeWidth="3"
    />
  );
}

function App() {
  const API_URL = "http://localhost:8000";

  // --------------------------
  // „Çπ„ÉÜ„Éº„ÉàÈ°ûÔºàË≠∞‰∫ãÈå≤Ë°®Á§∫„ÉªÁîüÊàêÔºâ
  // --------------------------
  const [notes, setNotes] = useState('„Åì„Åì„Å´ÊñáÂ≠óËµ∑„Åì„Åó„ÅåË°®Á§∫„Åï„Çå„Åæ„Åô');
  const [summary, setSummary] = useState('„Åì„Åì„Å´Ë≠∞‰∫ãÈå≤„ÉªÊîπÂñÑÊ°à„ÅåË°®Á§∫„Åï„Çå„Åæ„Åô');
  const [mindmapData, setMindmapData] = useState(null);
  const [title, setTitle] = useState('');

  const [recording, setRecording] = useState(false);
  const [recorded, setRecorded] = useState(false);
  const [showSaveModal, setShowSaveModal] = useState(false);
  const [loading, setLoading] = useState(false);
  const [saveStatus, setSaveStatus] = useState(null);

  // ‰øùÂ≠òÊ∏à„ÅøË≠∞‰∫ãÈå≤
  const [savedMinutes, setSavedMinutes] = useState([]);
  const [selectedMinute, setSelectedMinute] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  // Èå≤Èü≥Èñ¢ÈÄ£
  const mediaRecorderRef = useRef(null);
  const mediaStreamRef = useRef(null);
  const [audioChunks, setAudioChunks] = useState([]);
  const localChunksRef = useRef([]);

  // Èå≤Èü≥ÊôÇÈñì„Ç´„Ç¶„É≥„Çø„Éº
  const [recordingTime, setRecordingTime] = useState(0);
  const recordingTimerRef = useRef(null);

  // „É¢„Éº„Éâ: audio / text / chat
  const [mode, setMode] = useState("audio");
  const [inputTranscript, setInputTranscript] = useState("");

  // „ÉÅ„É£„ÉÉ„Éà„É¢„Éº„Éâ
  const [chatMessages, setChatMessages] = useState([]);
  const [chatInput, setChatInput] = useState("");
  const [chatLoading, setChatLoading] = useState(false);

  // --------------------------
  // 1) ‰øùÂ≠òÊ∏à„ÅøË≠∞‰∫ãÈå≤„ÇíÂèñÂæó
  // --------------------------
  const fetchSavedMinutes = async () => {
    try {
      const res = await fetch(`${API_URL}/get-minutes`);
      if (!res.ok) throw new Error('‰øùÂ≠ò„Åï„Çå„ÅüË≠∞‰∫ãÈå≤„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
      const data = await res.json();
      const sorted = data.minutes.sort((a, b) => b.id - a.id);
      setSavedMinutes(sorted);
    } catch (error) {
      console.error('Error fetching saved minutes:', error);
    }
  };

  useEffect(() => {
    fetchSavedMinutes();
  }, []);

  /**
   * „Éñ„É©„Ç¶„Ç∂„Åå„Çµ„Éù„Éº„Éà„Åô„ÇãÈå≤Èü≥mimeType„ÇíÈ†Ü„Å´Êé¢„Åô
   */
  function getSupportedMimeType() {
    const possibleTypes = [
      "audio/webm;codecs=opus",
      "audio/ogg;codecs=opus",
      "audio/webm",
      "audio/ogg"
    ];
    for (const t of possibleTypes) {
      if (MediaRecorder.isTypeSupported(t)) {
        return t;
      }
    }
    return "";
  }

  // Èå≤Èü≥ÊôÇÈñì„Çí mm:ss „Éï„Ç©„Éº„Éû„ÉÉ„Éà„Å´Â§âÊèõ
  const formatRecordingTime = (seconds) => {
    const m = Math.floor(seconds / 60);
    const s = seconds % 60;
    return `${m}:${s.toString().padStart(2, '0')}`;
  };

  // ====================================================
  // Èå≤Èü≥ÈñãÂßã
  // ====================================================
  const startRecording = async () => {
    setRecording(true);
    setRecorded(false);
    setAudioChunks([]);
    localChunksRef.current = [];
    setNotes('Èå≤Èü≥„ÅåÈñãÂßã„Åï„Çå„Åæ„Åó„Åü');
    setSummary('‰ºöË≠∞‰∏≠...');
    setRecordingTime(0);

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaStreamRef.current = stream;

      // Èå≤Èü≥ÊôÇÈñì„Ç´„Ç¶„É≥„ÉàÈñãÂßã
      recordingTimerRef.current = setInterval(() => {
        setRecordingTime(prev => prev + 1);
      }, 1000);

      const mimeType = getSupportedMimeType() || "audio/webm";
      const mediaRecorder = new MediaRecorder(stream, { mimeType });
      mediaRecorder.ondataavailable = (e) => {
        if (e.data.size > 0) {
          localChunksRef.current.push(e.data);
        }
      };
      mediaRecorder.onstop = async () => {
        // „Çø„Ç§„Éû„ÉºÂÅúÊ≠¢
        if (recordingTimerRef.current) {
          clearInterval(recordingTimerRef.current);
          recordingTimerRef.current = null;
        }

        const blob = new Blob(localChunksRef.current, { type: mimeType });
        localChunksRef.current = [];
        setAudioChunks((prev) => [...prev, blob]);
        console.log("üéôÔ∏è Èå≤Èü≥ÁµÇ‰∫Ü: Blob ‰ΩúÊàêÊ∏à„Åø", blob.size);

        setRecording(false);
        setRecorded(true);
        setNotes("Èå≤Èü≥„ÅåÂÅúÊ≠¢„Åó„Åæ„Åó„Åü„ÄÇÊñáÂ≠óËµ∑„Åì„Åó„ÇíÊ∫ñÂÇô‰∏≠...");

        // „Çµ„Éº„Éê„ÉºÈÄÅ‰ø°
        await sendAudioToServer(blob);
      };
      mediaRecorderRef.current = mediaRecorder;
      mediaRecorder.start();
      console.log("üéôÔ∏è Èå≤Èü≥ÈñãÂßã");
    } catch (err) {
      console.error('Èå≤Èü≥ÈñãÂßãÂ§±Êïó:', err);
      setNotes('Èå≤Èü≥ÈñãÂßã„Å´Â§±Êïó„Åó„Åæ„Åó„Åü: ' + err.message);
      setRecording(false);
    }
  };

  // ====================================================
  // Èå≤Èü≥ÂÅúÊ≠¢
  // ====================================================
  const stopRecording = async () => {
    if (recordingTimerRef.current) {
      clearInterval(recordingTimerRef.current);
      recordingTimerRef.current = null;
    }

    if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
      mediaRecorderRef.current.stop();
    }
    if (mediaStreamRef.current) {
      mediaStreamRef.current.getTracks().forEach((track) => track.stop());
    }
    console.log("üõë Èå≤Èü≥ÂÅúÊ≠¢Ë¶ÅÊ±Ç");
  };

  // --------------------------
  // Èå≤Èü≥„Éá„Éº„Çø„Çí„Çµ„Éº„Éê„Éº„Å´ÈÄÅ‰ø°
  // --------------------------
  const sendAudioToServer = async (audioBlob) => {
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('audio', new File([audioBlob], 'recording.webm', { type: audioBlob.type }));

      console.log("üì§ /transcribe „Å∏ POST. blobSize=", audioBlob.size);
      const res = await fetch(`${API_URL}/transcribe`, {
        method: 'POST',
        body: formData,
      });
      if (!res.ok) {
        throw new Error(`„Çµ„Éº„Éê„Éº„Ç®„É©„Éº: ${res.statusText}`);
      }
      const data = await res.json();
      console.log("üß† ÊñáÂ≠óËµ∑„Åì„Åó & Ë≠∞‰∫ãÈå≤ÁµêÊûú:", data);

      setNotes(data.formatted_transcript);
      setSummary(data.analysis);
      setMindmapData(data.mindmap);
      setTitle(data.title);
    } catch (err) {
      console.error('ÈÄö‰ø°„Ç®„É©„Éº:', err);
