import React, { useState, useEffect, useRef } from 'react';
import Tree from 'react-d3-tree';

// ======== MUI & „Åù„ÅÆ‰ªñ ========
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Drawer,
  List,
  ListItemButton,
  ListItemText,
  Box,
  Paper,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  CircularProgress,
  IconButton
} from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { motion, AnimatePresence } from 'framer-motion';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';

/** „ÉÜ„Éº„ÉûË®≠ÂÆö */
const theme = createTheme({
  palette: {
    primary: { main: '#1976d2' },
    secondary: { main: '#00f2fe' },
  },
  typography: {
    fontFamily: [
      'Montserrat',
      'Playfair Display',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
    ].join(','),
  },
});

// „Ç¢„Éã„É°„Éº„Ç∑„Éß„É≥Áî®„Éê„É™„Ç¢„É≥„Éà
const fadeInVariants = {
  hidden: { opacity: 0, y: -10 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.5 } },
};

function App() {
  const API_URL = "http://localhost:8000";

  // --------------------------
  // „Çπ„ÉÜ„Éº„ÉàÈ°ûÔºàË≠∞‰∫ãÈå≤Ë°®Á§∫„ÉªÁîüÊàêÔºâ
  // --------------------------
  const [notes, setNotes] = useState('„Åì„Åì„Å´ÊñáÂ≠óËµ∑„Åì„Åó„ÅåË°®Á§∫„Åï„Çå„Åæ„Åô');
  const [summary, setSummary] = useState('„Åì„Åì„Å´Ë≠∞‰∫ãÈå≤„ÉªÊîπÂñÑÊ°à„ÅåË°®Á§∫„Åï„Çå„Åæ„Åô');
  const [mindmapData, setMindmapData] = useState(null);
  const [title, setTitle] = useState('');
  const [recording, setRecording] = useState(false);
  const [recorded, setRecorded] = useState(false);
  const [showSaveModal, setShowSaveModal] = useState(false);
  const [loading, setLoading] = useState(false);
