      const data = await res.json();
      const sorted = data.minutes.sort((a, b) => b.id - a.id);
      setSavedMinutes(sorted);
    } catch (error) {
      console.error('Error fetching saved minutes:', error);
    }
  };

  useEffect(() => {
    fetchSavedMinutes();
  }, []);

  /**
   * „Éñ„É©„Ç¶„Ç∂„Åå„Çµ„Éù„Éº„Éà„Åô„ÇãÈå≤Èü≥mimeType„ÇíÈ†Ü„Å´Êé¢„Åô
   */
  function getSupportedMimeType() {
    const possibleTypes = [
      "audio/webm;codecs=opus",
      "audio/ogg;codecs=opus",
      "audio/webm",
      "audio/ogg"
    ];
    for (const t of possibleTypes) {
      if (MediaRecorder.isTypeSupported(t)) {
        return t;
      }
    }
    return "";
  }

  // ====================================================
  // Èå≤Èü≥ÈñãÂßã
  // ====================================================
  const startRecording = async () => {
    setRecording(true);
    setRecorded(false);
    setAudioChunks([]);
    localChunksRef.current = [];
    setNotes('Èå≤Èü≥„ÅåÈñãÂßã„Åï„Çå„Åæ„Åó„Åü');
    setSummary('‰ºöË≠∞‰∏≠...');

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaStreamRef.current = stream;

      const mimeType = getSupportedMimeType() || "audio/webm";
      const mediaRecorder = new MediaRecorder(stream, { mimeType });
      let localChunks = [];
      mediaRecorder.ondataavailable = (e) => {
        if (e.data.size > 0) {
          localChunksRef.current.push(e.data);
        }
      };
      mediaRecorder.onstop = () => {
        const blob = new Blob(localChunksRef.current, { type: mimeType });
        setAudioChunks((prev) => [...prev, blob]);
        localChunksRef.current = [];
        console.log("üéôÔ∏è Èå≤Èü≥ÁµÇ‰∫Ü: Blob ‰ΩúÊàêÊ∏à„Åø");
      };
      mediaRecorderRef.current = mediaRecorder;
      mediaRecorder.start();
      console.log("üéôÔ∏è Èå≤Èü≥ÈñãÂßã");
    } catch (err) {
      console.error('Èå≤Èü≥ÈñãÂßãÂ§±Êïó:', err);
      setNotes('Èå≤Èü≥ÈñãÂßã„Å´Â§±Êïó„Åó„Åæ„Åó„Åü: ' + err.message);
      setRecording(false);
    }
  };

