      const res = await fetch(`${API_URL}/transcribe`, {
        method: 'POST',
        body: formData,
      });
      if (!res.ok) throw new Error(`„Çµ„Éº„Éê„Éº„Ç®„É©„Éº: ${res.statusText}`);
      const data = await res.json();
      console.log("üß† Ë≠∞‰∫ãÈå≤ÁîüÊàêÁµêÊûú:", data);
