import React, { useState, useEffect, useRef } from 'react';
import Tree from 'react-d3-tree';

// ======== MUIã‚³ãƒ³ãƒãƒ¼ãƒãƒ³ãƒˆ & ãƒ•ãƒ¬ãƒ¼ãƒ ãƒ¯ãƒ¼ã‚¯ =========
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Drawer,
  List,
  ListItemButton,
  ListItemText,
  Box,
  Paper,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  CircularProgress,
  IconButton
} from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

// ======== Framer Motion for Animations =========
import { motion, AnimatePresence } from 'framer-motion';

// ======== ã‚¢ã‚¤ã‚³ãƒ³ =========
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import CloseIcon from '@mui/icons-material/Close';

/**
 * ãƒ†ãƒ¼ãƒžã‚’ä½œæˆï¼ˆéƒ½ä¼šçš„ãªãƒ–ãƒ«ãƒ¼Ã—ãƒ›ãƒ¯ã‚¤ãƒˆï¼‹ã‚„ã‚„è¿‘æœªæ¥æ„Ÿï¼‰
 */
const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#00f2fe',
    },
  },
  typography: {
    fontFamily: [
      'Montserrat',
      'Playfair Display',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
    ].join(','),
  },
});

/**
 * ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³ç”¨ã®ãƒãƒªã‚¢ãƒ³ãƒˆä¾‹
 */
const fadeInVariants = {
  hidden: { opacity: 0, y: -10 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.5 } },
};
const popInVariants = {
  hidden: { opacity: 0, scale: 0.9 },
  visible: { opacity: 1, scale: 1, transition: { duration: 0.4 } },
};

function App() {
  const API_URL = "http://localhost:8000";

  // --------------------------
  // ã‚¹ãƒ†ãƒ¼ãƒˆé¡žï¼ˆè­°äº‹éŒ²ä½œæˆéƒ¨åˆ†ï¼‰
  // --------------------------
  const [notes, setNotes] = useState('ã“ã“ã«æ–‡å­—èµ·ã“ã—ãŒè¡¨ç¤ºã•ã‚Œã¾ã™');
  const [summary, setSummary] = useState('ã“ã“ã«è­°äº‹éŒ²ãƒ»æ”¹å–„æ¡ˆãŒè¡¨ç¤ºã•ã‚Œã¾ã™');
  const [mindmapData, setMindmapData] = useState(null);
  const [title, setTitle] = useState('');

  const [recording, setRecording] = useState(false);
  const [recorded, setRecorded] = useState(false);
  const [showSaveModal, setShowSaveModal] = useState(false);
  const [loading, setLoading] = useState(false);
  const [saveStatus, setSaveStatus] = useState(null);

  const [savedMinutes, setSavedMinutes] = useState([]);
  const [selectedMinute, setSelectedMinute] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  // éŒ²éŸ³é–¢é€£
  const mediaRecorderRef = useRef(null);
  const [audioChunks, setAudioChunks] = useState([]);
  // æœ€æ–°ã®ãƒãƒ£ãƒ³ã‚¯ä¸€è¦§ã‚’å³æ™‚å‚ç…§ã™ã‚‹ãŸã‚ã® useRef
  const audioChunksRef = useRef([]);
  const [recordingTimerId, setRecordingTimerId] = useState(null);
  const [recordingDuration, setRecordingDuration] = useState(0);
  const durationIntervalRef = useRef(null);
  const mediaStreamRef = useRef(null);

  // --------------------------
  // ãƒ¢ãƒ¼ãƒ‰: audio | text | chat
  // --------------------------
  const [mode, setMode] = useState("audio");
  const [inputTranscript, setInputTranscript] = useState("");

  // --------------------------
  // ãƒãƒ£ãƒƒãƒˆãƒ¢ãƒ¼ãƒ‰ç”¨
  // --------------------------
  const [chatMessages, setChatMessages] = useState([]);
  const [chatInput, setChatInput] = useState("");

  // --------------------------
  // ã‚°ãƒ­ãƒ¼ãƒãƒ«å¤‰æ•°ï¼ˆæœ€å¾Œã®ãƒãƒ£ãƒ³ã‚¯ä¿å­˜å¾…æ©Ÿç”¨ã® resolve é–¢æ•°ï¼‰
  // --------------------------
  let resolveFinalChunk;

  // --------------------------
  // 1) ä¿å­˜æ¸ˆã¿è­°äº‹éŒ²ã‚’å–å¾—
  // --------------------------
  const fetchSavedMinutes = async () => {
    try {
      const res = await fetch(`${API_URL}/get-minutes`);
      if (!res.ok) throw new Error('ä¿å­˜ã•ã‚ŒãŸè­°äº‹éŒ²ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ');
      const data = await res.json();
      const sorted = data.minutes.sort((a, b) => b.id - a.id);
      setSavedMinutes(sorted);
    } catch (error) {
      console.error('Error fetching saved minutes:', error);
    }
  };

  useEffect(() => {
    fetchSavedMinutes();
  }, []);

  /**
   * ãƒ–ãƒ©ã‚¦ã‚¶ãŒã‚µãƒãƒ¼ãƒˆã™ã‚‹éŒ²éŸ³mimeTypeã‚’é †ã«æŽ¢ã™
   */
  function getSupportedMimeType() {
    const possibleTypes = [
      "audio/webm;codecs=opus",
      "audio/ogg;codecs=opus",
      "audio/webm",
      "audio/ogg"
    ];
    for (const t of possibleTypes) {
      if (MediaRecorder.isTypeSupported(t)) {
        return t;
      }
    }
    return "";
  }

  // ====================================================
  // éŒ²éŸ³é–‹å§‹ï¼ˆ10åˆ†ã”ã¨ã«ãƒãƒ£ãƒ³ã‚¯åˆ†å‰²ï¼‰
  // ====================================================
  const startRecording = async () => {
    setRecording(true);
    setRecorded(false);
    setAudioChunks([]);
    audioChunksRef.current = []; // åŒæœŸçš„ã«åˆæœŸåŒ–
    setRecordingDuration(0);
    setNotes('éŒ²éŸ³ãŒé–‹å§‹ã•ã‚Œã¾ã—ãŸ');
    setSummary('ä¼šè­°ä¸­...');

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaStreamRef.current = stream;
      const mimeType = getSupportedMimeType();
      if (!mimeType) {
        throw new Error("ãƒ–ãƒ©ã‚¦ã‚¶ãŒã‚µãƒãƒ¼ãƒˆã™ã‚‹éŒ²éŸ³ãƒ•ã‚©ãƒ¼ãƒžãƒƒãƒˆãŒã‚ã‚Šã¾ã›ã‚“ã€‚");
      }

      // MediaRecorder ã‚’ä½œæˆã™ã‚‹ãƒ˜ãƒ«ãƒ‘ãƒ¼é–¢æ•°
      const createRecorder = () => {
        const mediaRecorder = new MediaRecorder(stream, { mimeType });
        let localChunks = [];

        mediaRecorder.addEventListener("dataavailable", (e) => {
          if (e.data.size > 0) {
            localChunks.push(e.data);
          }
        });

        mediaRecorder.addEventListener("stop", () => {
          const blob = new Blob(localChunks, { type: mimeType });
          const file = new File([blob], `recording_part_${Date.now()}.webm`, { type: mimeType });
          setAudioChunks(prev => {
            const updated = [...prev, file];
            audioChunksRef.current = updated;
            console.log("ðŸ“¦ ãƒãƒ£ãƒ³ã‚¯ä¿å­˜:", updated);
            return updated;
          });
          localChunks = [];

          // å°‘ã—å¾…ã£ã¦ã‹ã‚‰æ¬¡ã® Recorder ã‚’é–‹å§‹ã—ã€resolve ã‚’å‘¼ã¶
          setTimeout(() => {
            if (recording) {
              const nextRecorder = createRecorder();
              mediaRecorderRef.current = nextRecorder;
              nextRecorder.start();
            }
            // æœ€å¾Œã®ãƒãƒ£ãƒ³ã‚¯å¾…æ©Ÿç”¨ Promise ã® resolve ã‚’å‘¼ã¶
            if (typeof resolveFinalChunk === "function") {
              console.log("âœ… æœ€å¾Œã®ãƒãƒ£ãƒ³ã‚¯ä¿å­˜å®Œäº† â†’ resolveFinalChunk()");
              resolveFinalChunk();
            }
          }, 200);
        });

        return mediaRecorder;
      };

      const firstRecorder = createRecorder();
      mediaRecorderRef.current = firstRecorder;
      firstRecorder.start();

      const chunkTimer = setInterval(() => {
        if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
          mediaRecorderRef.current.stop();
        }
      }, 600000);
      setRecordingTimerId(chunkTimer);

      const durationTimer = setInterval(() => {
        setRecordingDuration(prev => prev + 1);
      }, 1000);
      durationIntervalRef.current = durationTimer;

    } catch (err) {
      console.error('éŒ²éŸ³é–‹å§‹å¤±æ•—:', err);
      setNotes('éŒ²éŸ³é–‹å§‹ã«å¤±æ•—ã—ã¾ã—ãŸ: ' + err.message);
      setRecording(false);
    }
  };

  // ====================================================
  // éŒ²éŸ³åœæ­¢ & è‡ªå‹•è­°äº‹éŒ²ä½œæˆã¸ã®é€ä¿¡ï¼ˆonstop å®Œäº†ã‚’ Promise ã§å¾…æ©Ÿï¼‰
  // ====================================================
  const stopRecording = async () => {
    setRecording(false);
    setRecorded(true);
    setLoading(true); // â† ã“ã“ã‚’æ˜Žç¤ºçš„ã«å…¥ã‚Œã¦ãŠãã¨ UI å´ã§ã‚¿ã‚¤ãƒŸãƒ³ã‚°ãšã‚ŒãŒèµ·ãã¥ã‚‰ã„

    if (recordingTimerId) {
      clearInterval(recordingTimerId);
      setRecordingTimerId(null);
    }
    if (durationIntervalRef.current) {
      clearInterval(durationIntervalRef.current);
      durationIntervalRef.current = null;
    }

    // Promise ã‚’åˆ©ç”¨ã—ã¦æœ€å¾Œã®ãƒãƒ£ãƒ³ã‚¯ãŒç¢ºå®Ÿã«ä¿å­˜ã•ã‚Œã‚‹ã®ã‚’å¾…æ©Ÿ
    const waitForFinalChunk = () =>
      new Promise((resolve) => {
        if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
          resolveFinalChunk = resolve;
          mediaRecorderRef.current.stop();
        } else {
          resolve();
        }
      });

    if (mediaStreamRef.current) {
      mediaStreamRef.current.getTracks().forEach((track) => track.stop());
    }

    setNotes("éŒ²éŸ³ãŒåœæ­¢ã—ã¾ã—ãŸã€‚æ–‡å­—èµ·ã“ã—ã‚’æº–å‚™ä¸­...");

    await waitForFinalChunk();
      sendChunksToServer();

    // ä¸‡ãŒä¸€ã€5ç§’ä»¥ä¸ŠçµŒã£ã¦ã‚‚ãƒãƒ£ãƒ³ã‚¯ãŒä¿å­˜ã•ã‚Œãªã„å ´åˆã®ä¿é™º
    setTimeout(() => {
      if (audioChunksRef.current.length === 0) {
        console.warn("â° ãƒãƒ£ãƒ³ã‚¯ãŒä½œæˆã•ã‚Œãªã„ãŸã‚ã€å¼·åˆ¶çš„ã«é€ä¿¡");
        sendChunksToServer();
      }
    }, 5000);


  };

  // ====================================================
  // è¤‡æ•°ãƒãƒ£ãƒ³ã‚¯é€ä¿¡ â†’ è­°äº‹éŒ²ç”Ÿæˆ
  // ====================================================
  const sendChunksToServer = async () => {
    if (!audioChunksRef.current || audioChunksRef.current.length === 0) {
      console.warn('éŸ³å£°ãƒãƒ£ãƒ³ã‚¯ãŒã‚ã‚Šã¾ã›ã‚“');
      return;
    }
    try {
      setLoading(true);
      const formData = new FormData();
      audioChunksRef.current.forEach((file, index) => {
        formData.append('audios', file, file.name || `recording_part_${index}.webm`);
      });

      const res = await fetch(`${API_URL}/transcribe-chunks`, {
        method: 'POST',
        body: formData,
      });
      if (!res.ok) throw new Error(`ã‚µãƒ¼ãƒãƒ¼ã‚¨ãƒ©ãƒ¼: ${res.statusText}`);

      const data = await res.json();
      setNotes(data.formatted_transcript);
      setSummary(data.analysis);
      setMindmapData(data.mindmap);
      setTitle(data.title);

      setAudioChunks([]);
      audioChunksRef.current = [];
    } catch (error) {
      console.error('é€šä¿¡ã‚¨ãƒ©ãƒ¼:', error);
      setNotes('é€šä¿¡ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸï¼ˆãƒ•ã‚¡ã‚¤ãƒ«ã‚µã‚¤ã‚ºåˆ¶é™ãªã©ã®å¯èƒ½æ€§ã‚ã‚Šï¼‰');
    } finally {
      setLoading(false);
    }
  };

  // ====================================================
  // ãƒ†ã‚­ã‚¹ãƒˆãƒ¢ãƒ¼ãƒ‰: è­°äº‹éŒ²ä½œæˆ
  // ====================================================
  const sendTextToServer = async () => {
    if (!inputTranscript.trim()) {
      alert("ãƒ†ã‚­ã‚¹ãƒˆãŒç©ºã§ã™ã€‚");
      return;
    }
    try {
      setLoading(true);
      const res = await fetch(`${API_URL}/transcribe-text`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ raw_text: inputTranscript }),
      });
      if (!res.ok) throw new Error(`ã‚µãƒ¼ãƒãƒ¼ã‚¨ãƒ©ãƒ¼: ${res.statusText}`);

      const data = await res.json();
      setNotes(data.formatted_transcript);
      setSummary(data.analysis);
      setMindmapData(data.mindmap);
      setTitle(data.title);

      setRecorded(true);
    } catch (err) {
      console.error('ãƒ†ã‚­ã‚¹ãƒˆãƒ¢ãƒ¼ãƒ‰å¤±æ•—:', err);
      setNotes('ãƒ†ã‚­ã‚¹ãƒˆè­°äº‹éŒ²ã®ç”Ÿæˆä¸­ã«ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸã€‚');
    } finally {
      setLoading(false);
    }
  };

  // ====================================================
  // è­°äº‹éŒ²ä¿å­˜
  // ====================================================
  const saveToDatabase = async () => {
    try {
      const res = await fetch(`${API_URL}/save-minutes`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          formatted_transcript: notes,
          analysis: summary,
          mindmap: mindmapData,
          title: title,
        }),
      });
      if (!res.ok) {
        const errorText = await res.text();
